mapper.select:
select t.id, t.name, cu.name customerId, t.stage, t.type, u.name owner, t.source, con.fullname contactsId from tbl_tran t join tbl_customer cu on t.customerId = cu.id join tbl_contacts con on t.contactsId = con.id join tbl_user u on t.owner = u.id
u.name like '%' #{owner} '%'
and t.name like '%' #{name} '%'
and t.stage like '%' #{stage} '%'
and t.type like '%' #{type} '%'
and t.source like '%' #{source} '%'
and t.customerId=#{customerId}
and t.contactsId=#{contactsId}
limit #{skipCount},#{pageSize};

select count(*) value, stage name
from tbl_tran
group by stage;
